renderHTML:
__ht__function(data, gv, cache) {
let myChart;
let option = {
    textStyle: { color: 'rgba(255,255,255,0.8)'},
    tooltip: {
        trigger: 'axis',
        axisPointer: {
            type: 'shadow',
            label: {
                backgroundColor: '#283b56'
            }
        }
    },
    legend: {
        textStyle: {color: 'rgba(255,255,255,0.8)'},
        data:['日供热量', '室外平均温度']
    },
    grid: {
        top: 60,
        width: '93%',
        bottom: 0,
        left: 5,
        right: 5,
        containLabel: true
    },
    toolbox: {
        show: true,
        feature: {
            // dataView: {readOnly: false},
            // restore: {},
            saveAsImage: {}
        }
    },
    dataZoom: {
        show: false,
        start: 0,
        end: 100
    },
    xAxis: [
        {
            type: 'category',
            boundaryGap: true,
            data: (function (){
                var now = new Date();
                var res = [];
                var len = 10;
                while (len--) {
                    res.unshift(now.toLocaleTimeString().replace(/^\D*/,''));
                    now = new Date(now - 2000 );
                }
                return res;
            })()
        },
        {
            type: 'category',
            boundaryGap: true,
            data:[]
        }
    ],
    yAxis: [
        {
            type: 'value',
            scale: true,
            name: '温度',
            max: 30,
            min: -30,
            boundaryGap: [0.2, 0.2]
        },
        {
            type: 'value',
            scale: true,
            name: '热量',
            max: 1200,
            min: 0,
            boundaryGap: [0.2, 0.2]
        }
    ],
    series: [
        {
            name:'日供热量',
            type:'bar',
            color: 'rgba(27,85,245,0.6)',
            xAxisIndex: 1,
            yAxisIndex: 1,
            data:(function (){
                var res = [];
                var len = 10;
                while (len--) {
                    res.push(Math.round(Math.random() * 1000));
                }
                return res;
            })()
        },
        {
            name:'室外平均温度',
            type:'line',
            color: 'rgb(224,118,25)',
            data:(function (){
                var res = [];
                var len = 0;
                while (len < 10) {
                    res.push((Math.random()*20 -0 ).toFixed(1) - 10);
                    len++;
                }
                return res;
            })()
        }
    ]
};

let count = 11;









    if (!cache.htmlView) {
        cache.dom= document.createElement('div');
        myChart = cache.echart = echarts.init(cache.dom);

        // layoutHTML will be called when the data needs to be drawing
        cache.dom.layoutHTML = function () {
            gv.layoutHTML(data, cache.dom, true);
            cache.echart.resize();
        };
    }

    // update data
    //option.title.text = data.a('myname');
    cache.echart.setOption(option);

    // html for showing
    return cache.dom;
}
