renderHTML:
__ht__function(data, gv, cache) {

option = {
    textStyle: { color: 'rgba(255,255,255,0.8)'},
    tooltip: {
        trigger: 'axis',
        axisPointer: {
            type: 'shadow'
        }
    },
    legend: {
        textStyle: {color: 'rgba(255,255,255,0.8)'},
        data: ['2017年', '2018年']
    },
   grid: {
        top: 60,
        width: '93%',
        bottom: 0,
        left: 5,
        right: 5,
        containLabel: true
    },
    toolbox: {
        show: true,
        feature: {
            // dataView: {readOnly: false},
            // restore: {},
            saveAsImage: {}
        }
    },
    yAxis: {
        type: 'value',
        name: '单耗',
        boundaryGap: [0, 0.01]
    },
    xAxis: {
        type: 'category',
        data: ['站1','站2','站3','站4','站5','站6']
    },
    series: [
        {
            name: '2017年',
            type: 'bar',
            color: 'rgba(27,85,245,0.6)',
            data: [182, 234, 290, 1049, 1317, 2002]
        },
        {
            name: '2018年',
            type: 'bar',
            color: 'rgb(224,118,25)',
            data: [193, 234, 310, 1215, 1341, 1818]
        }
    ]
};



    if (!cache.htmlView) {
        cache.dom= document.createElement('div');
        myChart = cache.echart = echarts.init(cache.dom);

        // layoutHTML will be called when the data needs to be drawing
        cache.dom.layoutHTML = function () {
            gv.layoutHTML(data, cache.dom, true);
            cache.echart.resize();
        };
    }

    // update data
    //option.title.text = data.a('myname');
    cache.echart.setOption(option);

    // html for showing
    return cache.dom;
}
